<!DOCTYPE html><html><body> <?phpini_set('display_errors',1);error_reporting(E_ALL);// save categories$string = file_get_contents("data/categories.json");$json = json_decode($string, true);foreach ($json as $category => $data) {    save_category($data);		if(count($data['children']) > 0) {		foreach($data['children'] as $children => $data2) {			save_category($data2);		}	}}// save news$string = file_get_contents("data/news.json");$json = json_decode($string, true);foreach ($json as $news => $data) {    save_other("news", $data);}// save forums$string = file_get_contents("data/forums.json");$json = json_decode($string, true);foreach ($json as $news => $data) {    save_other("forums", $data);}// save blogs$string = file_get_contents("data/blogs.json");$json = json_decode($string, true);foreach ($json as $news => $data) {    save_other("blogs", $data);}function save_category($category) {	$conn = new mysqli('localhost', 'root', '', 'livee');	if($conn->connect_errno > 0){		die('Unable to connect to database [' . $conn->connect_error . ']');	}	mysqli_query($conn, "set character_set_client='utf8'");		$sql = "INSERT INTO category (name, alias, type, ord)	VALUES ('".$category['name']."', '".$category['alias']."', '".$category['type']."', '".$category['ord']."')";		if (mysqli_query($conn, $sql) === TRUE) {		echo "Category inserted successfully\r\n";	} else {		echo "Error iserting category: " . $conn->error . "\r\n";	}	mysqli_close($conn);}function save_other($table, $item) {	$conn = new mysqli('localhost', 'root', '', 'livee');	if($conn->connect_errno > 0){		die('Unable to connect to database [' . $conn->connect_error . ']');	}	mysqli_query($conn, "set character_set_client='utf8'");		$sql = "INSERT INTO ".$table." (title, category, body)	VALUES ('".$item['title']."', '".$item['category']."', '".$item['body']."')";		if (mysqli_query($conn, $sql) === TRUE) {		echo "Item inserted successfully\r\n";	} else {		echo "Error iserting item: " . $conn->error . "\r\n";	}	mysqli_close($conn);}?> </body></html>